/* ============ COLOR PALETTE ============ */
:root {
    --blue-day: #306AF2;
    --blue-night: #4789E5;
    --red-day: #F8333C;
    --red-night: #ee353e;
    /* Day */
    --day-darker: #7d7d7d;
    --day-dark: #B7B7B7;
    --day-light: #DDDDDD;
    --day-lighter: #EAEAEA;
    --day-lightest: #FFFFFF;
    /* Night */
    --night-darkest: #1E2029;
    --night-darker: #282a36;
    --night-dark: #373844;
    --night-light: ##6272a4;
    --night-lighter: #e2e2dc
}
